}

.product-footer {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 1rem;
}

.product-price {
    font-size: 1.8rem;
    font-weight: 900;
    color: #39FF14;
    text-shadow: 0 0 10px rgba(57, 255, 20, 0.3);
}

.add-to-cart-btn {
    padding: 0.75rem 1.5rem;
    background: linear-gradient(135deg, #39FF14 0%, #2ecc11 100%);
    border: none;
    color: #000000;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 1px;
    cursor: pointer;
    transition: all 0.3s ease;
