/* Product Gallery Styles */
.product-gallery {
    min-height: 100vh;
    padding: 4rem 2rem;
    background: linear-gradient(135deg, #000000 0%, #1a1a1a 100%);
}

.gallery-header {
    text-align: center;
    margin-bottom: 3rem;
}

.gallery-title {
    font-size: 3.5rem;
    font-weight: 900;
    color: #ffffff;
    margin-bottom: 1rem;
    letter-spacing: 2px;
}

.gallery-title .accent {
    color: #39FF14;
    text-shadow: 0 0 20px rgba(57, 255, 20, 0.5);
}

.gallery-subtitle {
    font-size: 1.2rem;
    color: #888;
    text-transform: uppercase;
    letter-spacing: 3px;
}

.gallery-controls {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 3rem;
    flex-wrap: wrap;
    gap: 1.5rem;
}

.filter-buttons {
    display: flex;
    gap: 1rem;
    flex-wrap: wrap;
}

.filter-buttons button {
    padding: 0.75rem 1.5rem;
    background: rgba(255, 255, 255, 0.05);
    border: 2px solid rgba(57, 255, 20, 0.3);
    color: #ffffff;
    font-weight: 600;
    text-transform: uppercase;
    letter-spacing: 1px;
    cursor: pointer;
    transition: all 0.3s ease;
    border-radius: 4px;
}

.filter-buttons button:hover {
    background: rgba(57, 255, 20, 0.1);
    border-color: #39FF14;
    transform: translateY(-2px);
}

.filter-buttons button.active {
    background: #39FF14;
    color: #000000;
    border-color: #39FF14;
    box-shadow: 0 0 20px rgba(57, 255, 20, 0.4);
}

.sort-select {
    padding: 0.75rem 1.5rem;
    background: rgba(255, 255, 255, 0.05);
    border: 2px solid rgba(57, 255, 20, 0.3);
    color: #ffffff;
    font-weight: 600;
    cursor: pointer;
    border-radius: 4px;
    transition: all 0.3s ease;
}

.sort-select:hover {
    border-color: #39FF14;
}

.sort-select option {
    background: #1a1a1a;
    color: #ffffff;
}

.products-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(320px, 1fr));
    gap: 2rem;
    margin-bottom: 4rem;
}

.product-card {
    background: rgba(255, 255, 255, 0.03);
    border: 1px solid rgba(57, 255, 20, 0.2);
    border-radius: 8px;
    overflow: hidden;
    transition: all 0.4s ease;
    cursor: pointer;
}

.product-card:hover {
    transform: translateY(-8px);
    border-color: #39FF14;
    box-shadow: 0 10px 40px rgba(57, 255, 20, 0.2);
}

.product-image-container {
    position: relative;
    width: 100%;
    height: 320px;
    background: linear-gradient(135deg, #0a0a0a 0%, #1a1a1a 100%);
    overflow: hidden;
}

.product-image {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.4s ease;
}

.product-card:hover .product-image {
    transform: scale(1.05);
}

.product-placeholder {
    width: 100%;
    height: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    background: linear-gradient(135deg, #1a1a1a 0%, #2a2a2a 100%);
}

.placeholder-icon {
    font-size: 4rem;
    margin-bottom: 1rem;
    opacity: 0.3;
}

.placeholder-text {
    color: #666;
    font-size: 0.9rem;
    text-align: center;
    padding: 0 1rem;
}

.image-toggle {
    position: absolute;
    bottom: 1rem;
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    gap: 0.5rem;
    background: rgba(0, 0, 0, 0.8);
    padding: 0.5rem;
    border-radius: 4px;
    opacity: 0;
    transition: opacity 0.3s ease;
}

.product-card:hover .image-toggle {
    opacity: 1;
}

.image-toggle button {
    padding: 0.4rem 0.8rem;
    background: rgba(255, 255, 255, 0.1);
    border: 1px solid rgba(57, 255, 20, 0.3);
    color: #ffffff;
    font-size: 0.75rem;
    font-weight: 600;
    text-transform: uppercase;
    cursor: pointer;
    transition: all 0.3s ease;
    border-radius: 2px;
}

.image-toggle button.active {
    background: #39FF14;
    color: #000000;
    border-color: #39FF14;
}

.product-badge {
    position: absolute;
    top: 1rem;
    right: 1rem;
    background: #39FF14;
    color: #000000;
    padding: 0.4rem 0.8rem;
    font-size: 0.75rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 1px;
    border-radius: 4px;
    box-shadow: 0 0 15px rgba(57, 255, 20, 0.5);
}

.product-info {
    padding: 1.5rem;
}

.product-category {
    color: #39FF14;
    font-size: 0.75rem;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 2px;
    margin-bottom: 0.5rem;
}

.product-name {
    font-size: 1.4rem;
    font-weight: 800;
    color: #ffffff;
    margin-bottom: 0.75rem;
    letter-spacing: 1px;
}

.product-description {
    color: #888;
    font-size: 0.9rem;
    line-height: 1.6;
    margin-bottom: 1.5rem;
}

.product-footer {
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 1rem;
}

.product-price {
    font-size: 1.8rem;
    font-weight: 900;
    color: #39FF14;
    text-shadow: 0 0 10px rgba(57, 255, 20, 0.3);
}

.add-to-cart-btn {
    padding: 0.75rem 1.5rem;
    background: linear-gradient(135deg, #39FF14 0%, #2ecc11 100%);
    border: none;
    color: #000000;
    font-weight: 700;
    text-transform: uppercase;
    letter-spacing: 1px;
    cursor: pointer;
    transition: all 0.3s ease;
    border-radius: 4px;
    box-shadow: 0 4px 15px rgba(57, 255, 20, 0.3);
}

.add-to-cart-btn:hover:not(:disabled) {
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(57, 255, 20, 0.5);
}

.add-to-cart-btn:disabled {
    background: rgba(255, 255, 255, 0.1);
    color: #666;
    cursor: not-allowed;
    box-shadow: none;
}

.no-products {
    text-align: center;
    padding: 4rem 2rem;
    color: #666;
    font-size: 1.2rem;
}

/* Responsive Design */
@media (max-width: 768px) {
    .gallery-title {
        font-size: 2.5rem;
    }

    .gallery-controls {
        flex-direction: column;
        align-items: stretch;
    }

    .filter-buttons {
        justify-content: center;
    }

    .products-grid {
        grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
        gap: 1.5rem;
    }

    .product-footer {
        flex-direction: column;
        align-items: stretch;
    }

    .add-to-cart-btn {
        width: 100%;
    }
}

import React, { useState } from 'react';
import { products, Product } from '../data/products';

interface ProductCardProps {
    product: Product;
    onAddToCart?: (product: Product) => void;
}

const ProductCard: React.FC<ProductCardProps> = ({ product, onAddToCart }) => {
    const [imageView, setImageView] = useState<'studio' | 'lifestyle'>('studio');
    const [imageError, setImageError] = useState(false);

    const currentImage = imageView === 'lifestyle' && product.imageLifestyle
        ? product.imageLifestyle
        : product.image;

    return (
        <div className="product-card group">
            <div className="product-image-container">
                {imageError ? (
                    <div className="product-placeholder">
                        <div className="placeholder-icon">ðŸ“¦</div>
                        <p className="placeholder-text">{product.name}</p>
                    </div>
                ) : (
                    <img
                        src={currentImage}
                        alt={product.name}
                        className="product-image"
                        onError={() => setImageError(true)}
                    />
                )}

                {product.imageLifestyle && !imageError && (
                    <div className="image-toggle">
                        <button
                            onClick={() => setImageView('studio')}
                            className={imageView === 'studio' ? 'active' : ''}
                        >
                            Studio
                        </button>
                        <button
                            onClick={() => setImageView('lifestyle')}
                            className={imageView === 'lifestyle' ? 'active' : ''}
                        >
                            Lifestyle
                        </button>
                    </div>
                )}

                {product.featured && (
                    <span className="product-badge">Featured</span>
                )}
            </div>

            <div className="product-info">
                <div className="product-category">{product.subcategory}</div>
                <h3 className="product-name">{product.name}</h3>
                <p className="product-description">{product.description}</p>

                <div className="product-footer">
                    <div className="product-price">â‚¬{product.price.toFixed(2)}</div>
                    <button
                        className="add-to-cart-btn"
                        onClick={() => onAddToCart?.(product)}
                        disabled={!product.inStock}
                    >
                        {product.inStock ? 'Add to Cart' : 'Out of Stock'}
                    </button>
                </div>
            </div>
        </div>
    );
};

interface ProductGalleryProps {
    onAddToCart?: (product: Product) => void;
}

export const ProductGallery: React.FC<ProductGalleryProps> = ({ onAddToCart }) => {
    const [filter, setFilter] = useState<'all' | 'supplement' | 'gear'>('all');
    const [sortBy, setSortBy] = useState<'featured' | 'price-asc' | 'price-desc' | 'name'>('featured');

    const filteredProducts = products.filter(p =>
        filter === 'all' || p.category === filter
    );

    const sortedProducts = [...filteredProducts].sort((a, b) => {
        switch (sortBy) {
            case 'featured':
                if (a.featured && !b.featured) return -1;
                if (!a.featured && b.featured) return 1;
                return 0;
            case 'price-asc':
                return a.price - b.price;
            case 'price-desc':
                return b.price - a.price;
            case 'name':
                return a.name.localeCompare(b.name);
            default:
                return 0;
        }
    });

    return (
        <div className="product-gallery">
            <div className="gallery-header">
                <h1 className="gallery-title">
                    STRYDA <span className="accent">Products</span>
                </h1>
                <p className="gallery-subtitle">Elite Performance. Premium Quality.</p>
            </div>

            <div className="gallery-controls">
                <div className="filter-buttons">
                    <button
                        onClick={() => setFilter('all')}
                        className={filter === 'all' ? 'active' : ''}
                    >
                        All Products
                    </button>
                    <button
                        onClick={() => setFilter('supplement')}
                        className={filter === 'supplement' ? 'active' : ''}
                    >
                        Supplements
                    </button>
                    <button
                        onClick={() => setFilter('gear')}
                        className={filter === 'gear' ? 'active' : ''}
                    >
                        Gear
                    </button>
                </div>

                <select
                    value={sortBy}
                    onChange={(e) => setSortBy(e.target.value as any)}
                    className="sort-select"
                >
                    <option value="featured">Featured</option>
                    <option value="price-asc">Price: Low to High</option>
                    <option value="price-desc">Price: High to Low</option>
                    <option value="name">Name</option>
                </select>
            </div>

            <div className="products-grid">
                {sortedProducts.map(product => (
                    <ProductCard
                        key={product.id}
                        product={product}
                        onAddToCart={onAddToCart}
                    />
                ))}
            </div>

            {sortedProducts.length === 0 && (
                <div className="no-products">
                    <p>No products found</p>
                </div>
            )}
        </div>
    );
};

export default ProductGallery;

import React, { createContext, useContext, useState, type ReactNode } from 'react';
import type { Product } from '../data/products';

export interface CartItem extends Product {
    quantity: number;
}

interface CartContextType {
    cart: CartItem[];
    addToCart: (product: Product, quantity?: number) => void;
    removeFromCart: (productId: string) => void;
    clearCart: () => void;
    totalItems: number;
    total: number;
}

const CartContext = createContext<CartContextType | undefined>(undefined);

export const CartProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
    const [cart, setCart] = useState<CartItem[]>([]);

    const addToCart = (product: Product, quantity: number = 1) => {
        setCart((prev) => {
            const existing = prev.find((item) => item.id === product.id);
            if (existing) {
                return prev.map((item) =>
                    item.id === product.id ? { ...item, quantity: item.quantity + quantity } : item
                );
            }
            return [...prev, { ...product, quantity }];
        });
    };

    const removeFromCart = (productId: string) => {
        setCart((prev) => prev.filter((item) => item.id !== productId));
    };

    const clearCart = () => {
        setCart([]);
    };

    const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);
    const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

    return (
        <CartContext.Provider value={{ cart, addToCart, removeFromCart, clearCart, totalItems, total }}>
            {children}
        </CartContext.Provider>
    );
};

export const useCart = () => {
    const context = useContext(CartContext);
    if (!context) {
        throw new Error('useCart must be used within a CartProvider');
    }
    return context;
};

@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&family=Space+Grotesk:wght@300;400;500;600;700&display=swap');
@import "tailwindcss";

@theme {
  --color-primary: #d9ff00;
  --color-background-dark: #000000;
  --color-background-light: #ffffff;
  --color-surface-dark: #121212;

  --font-display: "Space Grotesk", sans-serif;
  --font-body: "Inter", sans-serif;
}

@layer base {
  body {
    font-family: var(--font-body);
    @apply bg-background-dark text-white;
  }

  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    font-family: var(--font-display);
  }
}

@layer utilities {
  .text-glow:hover {
    text-shadow: 0 0 10px rgba(217, 255, 0, 0.5);
  }

  .neon-border {
    border: 1px solid #d9ff00;
  }

  .carbon-texture {
    background-image: radial-gradient(#1a1a1a 0.5px, transparent 0.5px);
    background-size: 4px 4px;
  }

  /* Custom Scrollbar */
  .custom-scrollbar::-webkit-scrollbar {
    width: 6px;
  }

  .custom-scrollbar::-webkit-scrollbar-track {
    background: #000;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb {
    background: #333;
    border-radius: 3px;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb:hover {
    background: #d9ff00;
  }
}

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { Shield, Lock } from 'lucide-react';

export const Checkout: React.FC = () => {
    const { cart, total, clearCart } = useCart();
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        address: '',
        city: '',
        zip: '',
        cardNumber: '',
        expiry: '',
        cvc: ''
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        // Simulate API call
        setTimeout(() => {
            clearCart();
            navigate('/success');
        }, 1500);
    };

    if (cart.length === 0) {
        return (
            <div className="min-h-[50vh] flex items-center justify-center bg-background-dark">
                <div className="text-center">
                    <h2 className="text-2xl font-black uppercase italic mb-4 text-white">Your Cart is Empty</h2>
                    <button onClick={() => navigate('/products')} className="text-primary font-bold uppercase tracking-widest hover:underline">Return to Shop</button>
                </div>
            </div>
        );
    }

    return (
        <div className="bg-background-dark min-h-screen py-12">
            <div className="container mx-auto px-6 max-w-6xl">
                <h1 className="text-4xl md:text-5xl font-black uppercase italic tracking-tighter mb-12 text-white">Checkout</h1>

                <div className="grid grid-cols-1 lg:grid-cols-12 gap-12">
                    {/* Form Section */}
                    <div className="lg:col-span-7 space-y-8">
                        <form onSubmit={handleSubmit} className="space-y-8">
                            {/* Shipping Info */}
                            <section>
                                <h3 className="text-lg font-bold uppercase tracking-widest text-primary mb-6 flex items-center gap-2">
                                    <span className="bg-primary text-black w-6 h-6 flex items-center justify-center rounded-full text-xs">1</span>
                                    Shipping Details
                                </h3>
                                <div className="grid grid-cols-2 gap-4">
                                    <input required name="firstName" placeholder="First Name" onChange={handleChange} className="bg-surface-dark border border-white/10 p-4 text-white placeholder-white/40 focus:border-primary outline-none" />
                                    <input required name="lastName" placeholder="Last Name" onChange={handleChange} className="bg-surface-dark border border-white/10 p-4 text-white placeholder-white/40 focus:border-primary outline-none" />
                                    <input required name="email" type="email" placeholder="Email Address" onChange={handleChange} className="col-span-2 bg-surface-dark border border-white/10 p-4 text-white placeholder-white/40 focus:border-primary outline-none" />
                                    <input required name="address" placeholder="Street Address" onChange={handleChange} className="col-span-2 bg-surface-dark border border-white/10 p-4 text-white placeholder-white/40 focus:border-primary outline-none" />
                                    <input required name="city" placeholder="City" onChange={handleChange} className="bg-surface-dark border border-white/10 p-4 text-white placeholder-white/40 focus:border-primary outline-none" />
                                    <input required name="zip" placeholder="ZIP Code" onChange={handleChange} className="bg-surface-dark border border-white/10 p-4 text-white placeholder-white/40 focus:border-primary outline-none" />
                                </div>
                            </section>

                            {/* Payment Info */}
                            <section>
                                <h3 className="text-lg font-bold uppercase tracking-widest text-primary mb-6 flex items-center gap-2">
                                    <span className="bg-primary text-black w-6 h-6 flex items-center justify-center rounded-full text-xs">2</span>
                                    Payment
                                </h3>
                                <div className="bg-surface-dark p-6 border border-white/10 space-y-4">
                                    <div className="flex items-center gap-2 mb-4 text-green-400 text-xs uppercase tracking-widest font-bold">
                                        <Lock className="w-3 h-3" /> Secure SSL Connection
                                    </div>
                                    <input required name="cardNumber" placeholder="Card Number" onChange={handleChange} className="w-full bg-black/50 border border-white/10 p-4 text-white placeholder-white/40 focus:border-primary outline-none" />
                                    <div className="grid grid-cols-2 gap-4">
                                        <input required name="expiry" placeholder="MM/YY" onChange={handleChange} className="bg-black/50 border border-white/10 p-4 text-white placeholder-white/40 focus:border-primary outline-none" />
                                        <input required name="cvc" placeholder="CVC" onChange={handleChange} className="bg-black/50 border border-white/10 p-4 text-white placeholder-white/40 focus:border-primary outline-none" />
                                    </div>
                                </div>
                            </section>

                            <button type="submit" className="w-full bg-primary py-5 text-black font-black uppercase tracking-[0.2em] text-lg hover:bg-white transition-colors duration-300">
                                Place Order // ${(total + 9.99).toFixed(2)}
                            </button>
                        </form>
                    </div>

                    {/* Summary Section */}
                    <div className="lg:col-span-5">
                        <div className="bg-surface-dark p-8 border border-white/10 sticky top-24">
                            <h3 className="text-lg font-bold uppercase tracking-widest text-white mb-6">Order Summary</h3>
                            <div className="space-y-6 mb-8 max-h-[400px] overflow-y-auto pr-2 custom-scrollbar">
                                {cart.map((item) => (
                                    <div key={item.id} className="flex gap-4">
                                        <div className="w-16 h-16 bg-white/5 p-2 flex items-center justify-center">
                                            <img src={item.image} alt={item.name} className="w-full h-full object-contain" />
                                        </div>
                                        <div className="flex-1">
                                            <h4 className="font-bold text-sm text-white">{item.name}</h4>
                                            <p className="text-primary text-xs font-bold mt-1">${item.price.toFixed(2)} x {item.quantity}</p>
                                        </div>
                                        <div className="font-bold text-white">${(item.price * item.quantity).toFixed(2)}</div>
                                    </div>
                                ))}
                            </div>

                            <div className="space-y-3 pt-6 border-t border-white/10 text-sm">
                                <div className="flex justify-between text-white/60">
                                    <span>Subtotal</span>
                                    <span className="font-bold">${total.toFixed(2)}</span>
                                </div>
                                <div className="flex justify-between text-white/60">
                                    <span>Shipping</span>
                                    <span className="font-bold">$9.99</span>
                                </div>
                                <div className="flex justify-between text-xl font-black italic text-white pt-4 border-t border-white/10">
                                    <span>Total</span>
                                    <span className="text-primary">${(total + 9.99).toFixed(2)}</span>
                                </div>
                            </div>

                            <div className="mt-8 flex items-center gap-3 text-xs text-white/40">
                                <Shield className="w-4 h-4" />
                                <p>100% Satisfaction Guarantee. Returns accepted within 30 days.</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

import React from 'react';
import { Link } from 'react-router-dom';
import { CheckCircle } from 'lucide-react';

export const OrderSuccess: React.FC = () => {
    return (
        <div className="min-h-screen bg-background-dark flex items-center justify-center p-6">
            <div className="text-center max-w-xl">
                <div className="inline-flex items-center justify-center w-20 h-20 rounded-full bg-primary/10 mb-8">
                    <CheckCircle className="w-10 h-10 text-primary" />
                </div>
                <h1 className="text-5xl md:text-7xl font-black uppercase italic tracking-tighter text-white mb-6">
                    Order Confirmed
                </h1>
                <p className="text-white/60 text-lg mb-12">
                    Thank you for choosing Stryda. Your gear is pending dispatch. You will receive a confirmation email shortly.
                </p>
                <Link to="/" className="inline-block bg-white text-black py-4 px-8 font-black uppercase tracking-[0.2em] hover:bg-primary transition-colors">
                    Continue Shopping
                </Link>
            </div>
        </div>
    );
};

import React, { useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { products } from '../data/products';
import { Truck, Shield, Check } from 'lucide-react';

export const ProductDetail: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const product = products.find(p => p.id === id);
    const { addToCart } = useCart();
    const [selectedFlavor, setSelectedFlavor] = useState('Blue Raz');

    if (!product) {
        return (
            <div className="min-h-[50vh] flex items-center justify-center">
                <div className="text-center">
                    <h2 className="text-2xl font-black uppercase italic mb-4">Product Not Found</h2>
                    <Link to="/products" className="text-primary font-bold uppercase tracking-widest hover:underline">Back to Shop</Link>
                </div>
            </div>
        );
    }

    // Determine if product needs flavor selection (Supplements only)
    const hasFlavors = product.category === 'supplements';

    return (
        <div className="bg-background-dark min-h-screen">
            <main className="max-w-7xl mx-auto px-6 py-8">
                {/* Breadcrumbs */}
                <nav className="mb-8 md:mb-12">
                    <ol className="flex items-center gap-2 text-[11px] font-bold tracking-[0.2em] uppercase text-primary">
                        <li><Link to="/" className="hover:underline">Home</Link></li>
                        <li className="text-primary/40">/</li>
                        <li><Link to={`/products?category=${product.category}`} className="hover:underline">{product.category}</Link></li>
                        <li className="text-primary/40">/</li>
                        <li className="text-white truncate max-w-[200px]">{product.name}</li>
                    </ol>
                </nav>

                <div className="grid grid-cols-1 lg:grid-cols-12 gap-12 lg:gap-16">
                    {/* Left Column: Image */}
                    <div className="lg:col-span-7">
                        <div className="relative group bg-surface-dark p-8 md:p-12 aspect-square flex items-center justify-center border border-white/5 overflow-hidden">
                            <div className="absolute inset-0 bg-gradient-to-br from-primary/5 to-transparent opacity-50"></div>
                            <img
                                src={product.image}
                                alt={product.name}
                                className="relative z-10 w-full h-full object-contain drop-shadow-[0_20px_50px_rgba(0,0,0,0.8)]"
                            />
                            <div className="absolute bottom-8 left-8 border-l-2 border-primary pl-4">
                                <p className="text-[10px] uppercase tracking-widest text-primary font-bold">Stryda Authentic</p>
                                <p className="text-[10px] uppercase tracking-widest text-white/40">Verified Quality</p>
                            </div>
                        </div>
                    </div>

                    {/* Right Column: Details */}
                    <div className="lg:col-span-5 flex flex-col gap-8">
                        <div>
                            <h1 className="text-4xl md:text-6xl font-black uppercase tracking-tighter leading-[0.9] text-white italic">
                                {product.name}
                            </h1>
                            <p className="mt-4 text-primary font-bold tracking-[0.3em] text-sm italic">PREMIUM {product.category}</p>
                        </div>

                        <div className="flex items-baseline gap-4">
                            <span className="text-5xl font-black text-primary text-glow italic tracking-tighter">${product.price.toFixed(2)}</span>
                        </div>

                        <p className="text-white/70 leading-relaxed font-light">
                            {product.description}
                        </p>

                        <div className="space-y-6">
                            {/* Flavor Selection (Conditional) */}
                            {hasFlavors && (
                                <div>
                                    <label className="text-[10px] font-black uppercase tracking-widest text-white/60 mb-3 block">Select Flavor // 01</label>
                                    <div className="flex flex-wrap gap-2">
                                        {['Blue Raz', 'Watermelon', 'Fruit Punch'].map((flavor) => (
                                            <button
                                                key={flavor}
                                                onClick={() => setSelectedFlavor(flavor)}
                                                className={`px-6 py-3 border-2 font-black uppercase text-xs tracking-widest transition-all ${selectedFlavor === flavor
                                                        ? 'border-primary bg-primary text-black'
                                                        : 'border-white/10 hover:border-primary/50 text-white'
                                                    }`}
                                            >
                                                {flavor}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                            )}
                        </div>

                        {/* CTAs */}
                        <div className="space-y-3 pt-4">
                            <button
                                onClick={() => addToCart(product)}
                                className="w-full bg-primary py-5 text-black font-black uppercase tracking-[0.2em] text-lg hover:bg-white transition-colors duration-300"
                            >
                                ADD TO CART
                            </button>
                            <div className="flex items-center justify-center gap-2 text-white/40 py-2">
                                <Truck className="w-4 h-4" />
                                <span className="text-[10px] uppercase tracking-widest font-bold">Free Shipping over $75</span>
                            </div>
                        </div>

                        {/* Features/Highlights */}
                        <div className="grid grid-cols-1 gap-4 border-t border-white/10 pt-8">
                            <div className="flex gap-4 items-start">
                                <div className="bg-white/5 p-3 rounded-full"><Shield className="w-5 h-5 text-primary" /></div>
                                <div>
                                    <h5 className="font-bold uppercase text-sm mb-1">Clinic Dosing</h5>
                                    <p className="text-xs text-white/50">Formulated with scientifically backed ingredients at effective dosages.</p>
                                </div>
                            </div>
                            <div className="flex gap-4 items-start">
                                <div className="bg-white/5 p-3 rounded-full"><Check className="w-5 h-5 text-primary" /></div>
                                <div>
                                    <h5 className="font-bold uppercase text-sm mb-1">No Banned Substances</h5>
                                    <p className="text-xs text-white/50">Safe for competition. Tested for purity and potency.</p>
                                </div>
                            </div>
                        </div>

                    </div>
                </div>
            </main>
        </div>
    );
};
