import React from 'react';
import { Link } from 'react-router-dom';
import { ShoppingCart, PawPrint, Menu, X } from 'lucide-react';
import { useCart } from '../context/CartContext';


export const Header: React.FC = () => {
    const { totalItems, toggleCart } = useCart();
    const [isMenuOpen, setIsMenuOpen] = React.useState(false);

    return (
        <header className="sticky top-0 z-50 bg-white/80 backdrop-blur-md border-b border-gray-100">
            <div className="container mx-auto px-4 h-20 flex items-center justify-between">
                {/* Logo */}
                <Link to="/" className="flex items-center gap-2 group">
                    <div className="p-2 bg-primary rounded-full group-hover:scale-110 transition-transform duration-300">
                        <PawPrint className="w-6 h-6 text-text-dark" />
                    </div>
                    <span className="font-display font-bold text-2xl text-text-dark tracking-tight">
                        Techtonic<span className="text-secondary">Pets</span>
                    </span>
                </Link>

                {/* Desktop Nav */}
                <nav className="hidden md:flex items-center gap-8">
                    <Link to="/" className="font-body font-medium text-text-dark hover:text-secondary transition-colors">Home</Link>
                    <Link to="/products" className="font-body font-medium text-text-dark hover:text-secondary transition-colors">Shop</Link>
                    <Link to="/products?category=Food" className="font-body font-medium text-text-dark hover:text-secondary transition-colors">Food</Link>
                    <Link to="/products?category=Toys" className="font-body font-medium text-text-dark hover:text-secondary transition-colors">Toys</Link>
                </nav>

                {/* Actions */}
                <div className="flex items-center gap-4">
                    <Link
                        to="/checkout"
                        className="relative p-2 hover:bg-gray-100 rounded-full transition-colors"
                        aria-label="View Cart"
                    >
                        <ShoppingCart className="w-6 h-6 text-text-dark" />
                        {totalItems > 0 && (
                            <span className="absolute -top-1 -right-1 bg-secondary text-text-dark text-xs font-bold w-5 h-5 flex items-center justify-center rounded-full animate-bounce">
                                {totalItems}
                            </span>
                        )}
                    </Link>

                    {/* Mobile Menu Button */}
                    <button
                        className="md:hidden p-2 hover:bg-gray-100 rounded-full"
                        onClick={() => setIsMenuOpen(!isMenuOpen)}
                    >
                        {isMenuOpen ? <X /> : <Menu />}
                    </button>
                </div>
            </div>

            {/* Mobile Menu */}
            {isMenuOpen && (
                <div className="md:hidden absolute top-20 left-0 w-full bg-white border-b border-gray-100 p-4 flex flex-col gap-4 shadow-lg animate-in slide-in-from-top-5">
                    <Link to="/" className="font-body font-medium text-lg p-2 hover:bg-gray-50 rounded-lg">Home</Link>
                    <Link to="/products" className="font-body font-medium text-lg p-2 hover:bg-gray-50 rounded-lg">All Products</Link>
                    <Link to="/products?category=Food" className="font-body font-medium text-lg p-2 hover:bg-gray-50 rounded-lg">Food</Link>
                    <Link to="/products?category=Toys" className="font-body font-medium text-lg p-2 hover:bg-gray-50 rounded-lg">Toys</Link>
                </div>
            )}
        </header>
    );
};

import React from 'react';
import { Link } from 'react-router-dom';
import { ShoppingBag, Star } from 'lucide-react';
import type { Product } from '../data/products';
import { useCart } from '../context/CartContext';
import { cn } from '../lib/utils';

interface ProductCardProps {
    product: Product;
    className?: string;
}

export const ProductCard: React.FC<ProductCardProps> = ({ product, className }) => {
    const { addToCart } = useCart();

    return (
        <div className={cn("group bg-white rounded-2xl overflow-hidden shadow-sm hover:shadow-md transition-all duration-300 border border-gray-100 flex flex-col", className)}>
            {/* Image Container */}
            <div className="relative aspect-square overflow-hidden bg-gray-50">
                <Link to={`/products/${product.id}`}>
                    <img
                        src={product.image}
                        alt={product.name}
                        className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-500"
                    />
                </Link>
                {product.badge && (
                    <span className={cn(
                        "absolute top-4 left-4 px-3 py-1 text-xs font-bold rounded-full tracking-wider",
                        product.badge === 'NEW' ? "bg-secondary text-text-dark" : "bg-primary text-text-dark"
                    )}>
                        {product.badge}
                    </span>
                )}

                <button
                    onClick={() => addToCart(product)}
                    className="absolute bottom-4 right-4 p-3 bg-white rounded-full shadow-lg translate-y-full opacity-0 group-hover:translate-y-0 group-hover:opacity-100 transition-all duration-300 hover:bg-primary text-text-dark"
                    aria-label="Add to cart"
                >
                    <ShoppingBag size={20} />
                </button>
            </div>

            {/* Content */}
            <div className="p-5 flex flex-col flex-grow">
                <div className="flex items-center gap-1 mb-2 text-yellow-400">
                    <Star size={14} fill="currentColor" />
                    <span className="text-xs text-gray-500 font-medium ml-1">{product.rating}</span>
                </div>

                <Link to={`/products/${product.id}`} className="block mb-2">
                    <h3 className="font-display font-bold text-lg text-text-dark leading-snug group-hover:text-primary transition-colors line-clamp-2">
                        {product.name}
                    </h3>
                </Link>

                <div className="mt-auto flex items-center justify-between">
                    <span className="font-body font-bold text-xl text-text-dark">${product.price.toFixed(2)}</span>
                    <span className="text-xs font-medium text-gray-400 uppercase tracking-wide">{product.category}</span>
                </div>
            </div>
        </div>
    );
};

import React, { createContext, useContext, useState, type ReactNode } from 'react';
import type { Product } from '../data/products';

export interface CartItem extends Product {
    quantity: number;
}

interface CartContextType {
    cart: CartItem[];
    addToCart: (product: Product, quantity?: number) => void;
    removeFromCart: (productId: string) => void;
    clearCart: () => void;
    updateQuantity: (productId: string, quantity: number) => void;
    totalItems: number;
    total: number;
    isCartOpen: boolean;
    toggleCart: () => void;
}

const CartContext = createContext<CartContextType | undefined>(undefined);

export const CartProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
    const [cart, setCart] = useState<CartItem[]>([]);
    const [isCartOpen, setIsCartOpen] = useState(false);

    const addToCart = (product: Product, quantity = 1) => {
        setCart(currentCart => {
            const existingItem = currentCart.find(item => item.id === product.id);
            if (existingItem) {
                return currentCart.map(item =>
                    item.id === product.id
                        ? { ...item, quantity: item.quantity + quantity }
                        : item
                );
            }
            return [...currentCart, { ...product, quantity }];
        });
        setIsCartOpen(true);
    };

    const removeFromCart = (productId: string) => {
        setCart(currentCart => currentCart.filter(item => item.id !== productId));
    };

    const updateQuantity = (productId: string, quantity: number) => {
        setCart(currentCart => {
            if (quantity <= 0) {
                return currentCart.filter(item => item.id !== productId);
            }
            return currentCart.map(item =>
                item.id === productId ? { ...item, quantity } : item
            );
        });
    };

    const clearCart = () => setCart([]);
    const toggleCart = () => setIsCartOpen(prev => !prev);

    const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);
    const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

    return (
        <CartContext.Provider value={{
            cart,
            addToCart,
            removeFromCart,
            clearCart,
            updateQuantity,
            totalItems,
            total,
            isCartOpen,
            toggleCart
        }}>
            {children}
        </CartContext.Provider>
    );
};

export const useCart = () => {
    const context = useContext(CartContext);
    if (!context) {
        throw new Error('useCart must be used within a CartProvider');
    }
    return context;
};
